import React from "react";

const DisplayTodo = (props) => {
  return props.todos.map((todo, index) => (
    <div key={index}>
      <input type="checkbox" />
      {todo}
      <button
        onClick={() => {
          props.deleteTodo(index);
        }}
      >
        x
      </button>
    </div>
  ));
};
export default DisplayTodo;
