import React, { useState } from "react";
import "./App.css";
import InputField from "./components/inputField";

const App = () => {
  const [todo, setTodo] = useState("");
  const [todoListt, setTodoList] = useState([]);
  console.log(todo);
  const addTodo = (addedTodo) => {
    setTodoList([...todoListt, addedTodo]);
  };

  return (
    <div className="App">
      <InputField setTodo={setTodo} />
    </div>
  );
};

export default App;
